/******************************************/ /* TABLE PERSON */ /******************************************/ CREATE TABLE PERSON( sin DECIMAL(6,0) NOT NULL,name VARCHAR(30) NOT NULL,address VARCHAR(100) NOT NULL, telephone VARCHAR(14) NOT NULL,type VARCHAR(1) NOT NULL,CONSTRAINT PK_person PRIMARY KEY (sin) ); /******************************************/ /* TABLE CLIENT */ /******************************************/ CREATE TABLE CLIENT( cli_id DECIMAL(6,0) NOT NULL,sin DECIMAL(6,0) NOT NULL,employer_name VARCHAR(30) NOT NULL,CONSTRAINT PK_client PRIMARY KEY (cli_id),CONSTRAINT FK_client_person FOREIGN KEY (sin) REFERENCES PERSON(sin) ); /******************************************/ /* TABLE SELLER */ /******************************************/ CREATE TABLE SELLER( seller_id DECIMAL(6,0) NOT NULL,sin DECIMAL(6,0) NOT NULL,category VARCHAR(20) NOT NULL,CONSTRAINT PK_seller PRIMARY KEY (seller_id),CONSTRAINT FK_seller_person FOREIGN KEY (sin) REFERENCES PERSON(sin) ); /******************************************/ /* TABLE BENEFICIARY */ /******************************************/ CREATE TABLE BENEFICIARY( benef_id DECIMAL(6,0) NOT NULL,sin DECIMAL(6,0) NOT NULL,CONSTRAINT PK_beneficiary PRIMARY KEY (benef_id), CONSTRAINT FK_beneficiary_person FOREIGN KEY (sin) REFERENCES PERSON(sin) ); /**************************************************/ /* TABLE FINANCIAL_PRODUCT */ /**************************************************/ CREATE TABLE FINANCIAL_PRODUCT( fp_id DECIMAL(6,0) NOT NULL,cli_id DECIMAL(6,0) NOT NULL,seller_id DECIMAL (6,0) NOT NULL,sequence_nb DECIMAL(6,0) NOT NULL,purrchease_date VARCHAR(10) NOT NULL,type VARCHAR(1) NOT NULL,CONSTRAINT PK_financial_product PRIMARY KEY (fp_id), CONSTRAINT FK_fp_client FOREIGN KEY (cli_id) REFERENCES CLIENT(cli_id), CONSTRAINT FK_fp_seller FOREIGN KEY (seller_id) REFERENCES SELLER(seller_id) ); /******************************************/ /* TABLE RRSP */ /******************************************/ CREATE TABLE RRSP( rrsp_id DECIMAL(6,0) NOT NULL,fp_id DECIMAL(6,0) NOT NULL,interest_rate DECIMAL(6,2) NOT NULL,initial_amount DECIMAL(6,2) NOT NULL,term VARCHAR(20) NOT NULL,CONSTRAINT PK_rrsp PRIMARY KEY (rrsp_id),CONSTRAINT FK_rrsp_fp FOREIGN KEY (fp_id) REFERENCES FINANCIAL_PRODUCT (fp_id) ); /******************************************/ /* TABLE LI */ /******************************************/ CREATE TABLE LI( li_id DECIMAL(6,0) NOT NULL,fp_id DECIMAL(6,0) NOT NULL,benef_id DECIMAL (6,0) NOT NULL,monthly_fees DECIMAL(6,2) NOT NULL,insured_amount DECIMAL(6,2) NOT NULL,CONSTRAINT PK_li PRIMARY KEY (li_id),CONSTRAINT FK_li_fp FOREIGN KEY (fp_id) REFERENCES FINANCIAL_PRODUCT(fp_id),CONSTRAINT FK_li_beneficiary FOREIGN KEY (benef_id) REFERENCES BENEFICIARY(benef_id) ); 